function [datFileJump, datFileSerial, subCode, subPath] = getSubjectCode(expCode)%% Asks for subject-ID to create subject code.% input: exptstr (string) name of experiment.% % Be sure to not use subject codes longer than% 8 letters, as the Eyelink file may otherwise% not have the same name as the dat files here;% in the worst case, compromise previous files.% % 2017 by Martin Rolfs    newFile = 0;    while ~newFile        subCode = getID(expCode);        subPath = strcat('./1_Data/',subCode);        % create data file        datFileJump    = sprintf('%sJump.dat', subPath);        datFileSerial  = sprintf('%sSerial.dat', subPath);        if exist(datFileJump,'file')            o = input('>>>> This file exists already. Should I overwrite it [y / n]? \n','s');            if strcmp(o,'y')                newFile = 1;            end        else            newFile = 1;        end    end